import React, { useState } from 'react';
import Axios from 'axios';
import './AddWorkoutStyle.css';

const AddWorkout = () => {
    
    const [showMessage, setShowMessage] = useState(false); // State to control the visibility of the message
    const [exercise, setExercise] = useState("");
    const [sets, setSets] = useState(0);
    const [reps, setReps] = useState("");
    const [weight, setWeight] = useState("");
    const [exerciseList, setExerciseList] = useState([]);
    const [showExercises, setShowExercises] = useState(false); // State to track visibility of exercises
    const [tableName, setTableName] = useState(""); 
  
  
    useEffect(() => {
      if (showExercises) { // Fetch exercises only if showExercises is true
        getExercises();
      }
    }, [showExercises]); // Fetch exercises when showExercises state changes
  
    const getExercises = () => {
      Axios.get('http://localhost:3001/exercises')
        .then((response) => setExerciseList(response.data))
        .catch(error => console.error('Error fetching exercises:', error));
    };
  
    const deleteExercise = (id) => {
      Axios.delete(`http://localhost:3001/exercise/${id}`)
        .then(() => {
          // Remove the deleted exercise from the exerciseList state
          setExerciseList(exerciseList.filter(exercise => exercise.id !== id));
        })
        .catch(error => console.error('Error deleting exercise:', error));
    };
  
    const addFullExercise = () => {
      Axios.post('http://localhost:3001/create', {
        exercise: exercise,
        sets: sets,
        reps: reps,
        weight: weight
      }).then(() => {
        // Update the exerciseList state with the new exercise object
        setExerciseList([...exerciseList, {
          exercise: exercise,
          sets: sets,
          reps: reps,
          weight: weight
        }]);
        // Clear input fields after adding exercise
        setExercise("");
        setSets(0);
        setReps("");
        setWeight("");
      }).catch(error => {
        console.error('Error adding exercise:', error);
      });
    };

    const handleClick = () => {
        if (showMessage) {
            // If showMessage is true, close the message
            setShowMessage(false);
        } else {
            // If showMessage is false, create a new workout
            Axios.post('http://localhost:3001/createTable', {
                tableName: tableName,
            }).then(() => {
                setTableName('');
                // Show the message after creating the table
                setShowMessage(true);
            }).catch(error => {
                console.error('Error adding exercise:', error);
            });
        }
    };

    return (
        <div>
            <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)} placeholder="Enter Workout Name" style={{ display: !showMessage ? 'block' : 'none' }} />
            <button onClick={handleClick}>
                {showMessage ? 'Close' : 'Create New Workout'}
            </button>
            {showMessage && (
                <div> 
                <div>Add Your Exercises</div>
                <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Sets</th>
                <th>Reps</th>
                <th>Weight</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {exerciseList.map(exercise => (
                <tr key={exercise.id}>
                  <td>{exercise.name}</td>
                  <td>{exercise.sets}</td>
                  <td>{exercise.reps}</td>
                  <td>{exercise.weight}</td>
                  <td>
                    <button onClick={() => deleteExercise(exercise.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          </div>
            )}
        </div>
    );
};

export default AddWorkout;
