import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle, faO } from '@fortawesome/free-solid-svg-icons';

import './EditWorkoutStyles.css';

const DoWorkout = () => {
  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState(0);
  const [reps, setReps] = useState("");
  const [weight, setWeight] = useState("");
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data.map(exercise => ({ ...exercise, ticked: false })));
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName]);

  const deleteExercise = (id) => {
    Axios.delete(`http://localhost:3001/exercise/${id}`)
      .then(() => {
        // Remove the deleted exercise from the exerciseList state
        setTableData(tableData.filter(exercise => exercise.id !== id));
      })
      .catch(error => console.error('Error deleting exercise:', error));
  };




  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((exercise) => (
            <tr key={exercise.id} className={exercise.ticked ? 'ticked' : ''}>
              <td>{exercise.Exercise}</td>
              <td>{exercise.Sets}</td>
              <td>{exercise.Reps}</td>
              <td>{exercise.Weight}</td>
              <td>
                <button onClick={() => deleteExercise(exercise.id)}>
                  Delete 
                </button>
              </td>
            </tr>
          ))}
          <tr>
            <td> <input type="text" value={exercise} onChange={(event) => setExercise(event.target.value)} /></td>
            <td><input type="number" value={sets} onChange={(event) => setSets(event.target.value)} /></td>
            <td><input type="text" value={reps} onChange={(event) => setReps(event.target.value)} /></td>
            <td><input type="text" value={weight} onChange={(event) => setWeight(event.target.value)} /></td>
            <td><button onClick={addFullExercise}>Add Exercise</button></td>

          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default DoWorkout;
