import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import Axios from 'axios';

const EditWorkout = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [exerciseToUpdate, setExerciseToUpdate] = useState('');
  const [setsToUpdate, setSetsToUpdate] = useState(0);
  const [repsToUpdate, setRepsToUpdate] = useState('');
  const [weightToUpdate, setWeightToUpdate] = useState('');
  const [toUpdate, setToUpdate] = useState(false);
  const [updateId, setUpdateId] = useState('');

  useEffect(() => {
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        const data = response.data;
        setTableData(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName, tableData]);

  const deleteExercise = (id) => {
    Axios.delete(`http://localhost:3001/tables/${tableName}/delete/${id}`)
      .then(() => {
        setTableData(tableData.filter(exercise => exercise.id !== id));
      })
      .catch(error => console.error('Error deleting exercise:', error));
  };

  const toggleUpdate = (exercise) => {
    setExerciseToUpdate(exercise.Exercise);
    setSetsToUpdate(exercise.Sets);
    setRepsToUpdate(exercise.Reps);
    setWeightToUpdate(exercise.Weight);
    setUpdateId(exercise.id);
    setToUpdate(true);
  };

  const editExercise = (id) => {
    Axios.put(`http://localhost:3001/tables/${tableName}/edit`, {
      exercise: exerciseToUpdate,
      sets: setsToUpdate,
      reps: repsToUpdate,
      weight: weightToUpdate
    }).then(() => {
      setTableData(tableData.map(item => {
        if (item.id === id) {
          return {
            ...item,
            exercise: exerciseToUpdate,
            sets: setsToUpdate,
            reps: repsToUpdate,
            weight: weightToUpdate
          };
        }
        return item;
      }));
      setToUpdate(false);
      setUpdateId('');
      setExerciseToUpdate('');
      setSetsToUpdate(0);
      setRepsToUpdate('');
      setWeightToUpdate('');
    }).catch(error => {
      console.error('Error updating exercise:', error);
    });
  };

  return (
    <div className="bg-black py-8 h-screen">
      <div className="container mx-auto px-4">
        <h2 className="text-Green font-bold text-center text-2xl mb-4">{tableName}</h2>
        <div className="overflow-x-auto sm:overflow-x-visible">
          <div className="flex flex-col">
            <div className="flex bg-Green text-darkGray font-bold text-center py-2 px-4 rounded-md mb-4">
              {/* Header */}
            </div>

            {tableData.map((exercise) => 
              toUpdate && exercise.id === updateId ? 
              (
                <div className="flex bg-darkGray text-white py-2 px-4 rounded-md mb-2" key={exercise.id}>
                  {/* Update exercise fields */}
                </div>
              ) : (
                <div className="flex bg-darkGray text-lightGray text-center text-bold py-2 px-4n rounded-md mb-1" key={exercise.id}>
                  {/* Display exercise details */}
                </div>
              )
            )}

            <div className="flex bg-darkGray text-white py-2 px-4 rounded-md mb-2">
              {/* Add exercise fields */}
            </div>
          </div>
        </div>
      </div>
      <div className="mt-4 flex justify-center">
        <Link to="/" className="bg-Green text-white px-4 py-2 rounded-md hover:bg-green-600">Finish</Link>
      </div>
    </div>
  );
};

export default EditWorkout;
