import React, { useState } from 'react';
import Axios from 'axios';
import './AddWorkoutStyle.css';

const AddWorkout = () => {
    const [tableName, setTableName] = useState("");
    const [showMessage, setShowMessage] = useState(false); // State to control the visibility of the message

    const handleClick = () => {
        if (showMessage) {
            // If showMessage is true, close the message
            setShowMessage(false);
        } else {
            // If showMessage is false, create a new workout
            Axios.post('http://localhost:3001/createTable', {
                tableName: tableName,
            }).then(() => {
                setTableName('');
                // Show the message after creating the table
                setShowMessage(true);
            }).catch(error => {
                console.error('Error adding exercise:', error);
            });
        }
    };

    return (
        <div>
            <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)} placeholder="Enter Workout Name" style={{ display: showMessage ? 'block' : 'none' }} />
            <button onClick={handleClick}>
                {showMessage ? 'Close' : 'Create New Workout'}
            </button>
            {showMessage && (
                <div>hello</div>
            )}
        </div>
    );
};

export default AddWorkout;
