import React, { useEffect, useState } from 'react';
import { useHref } from 'react-router-dom'; // Import useHistory
import Axios from 'axios';

const AllWorkouts = () => {
  const [tableNames, setTableNames] = useState([]);
  const history = useHref(); // Initialize useHistory

  useEffect(() => {
    getTableNames();
  }, []);

  const getTableNames = () => {
    Axios.get('http://localhost:3001/tables')
      .then((response) => {
        setTableNames(response.data);
      })
      .catch((error) => {
        console.error('Error fetching table names:', error);
      });
  };

  const handleButtonClick = (tableName) => {
    // Construct the URL string with the table name
    const url = `/${tableName}`;
    // Navigate to the new page using useHistory
    history.push(url);
  };

  return (
    <div>
      <div>
        {tableNames.map((table, index) => (
          <div key={index}>
            {/* Use handleButtonClick function for button onClick */}
            <button onClick={() => handleButtonClick(table)}>{table}</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllWorkouts;
