import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import './doWorkoutStyles.css'

const DoWorkout = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checkedRows, setCheckedRows] =useState([])
  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data.map(exercise => ({ ...exercise, ticked: false })));
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName]);

  useEffect(() => {
    setCheckedRows(Array(tableData.length).fill(false));
  }, [tableData]);
  {/*checking that the array is intialized correctly
  useEffect(() => {
    console.log(checkedRows);
  }, [checkedRows]); */}

  const handleTick = (index) => {
    setTableData(prevTableData => {
      return prevTableData.map((exercise) => {
        if (exercise.id === index) {
          return { ...exercise, ticked: !exercise.ticked };
        }
        return exercise;
      });
    });
  };
  
  
  
  
  

  
  
  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
            <th>Completed</th>
          </tr>
        </thead>
        <tbody>
        {tableData.map((exercise) => {
  const exerciseId = exercise.id.to;
  return (
    <tr  className={checkedRows[exercise.id] ? 'ticked' : ''}>
      <td>{exercise.Exercisel}</td>
      <td>{exercise.Sets}</td>
      <td>{exercise.Reps}</td>
      <td>{exercise.Weight}</td>
      <td>
        <button onClick={() =>  handleTick(exercise.id)}>
          {exercise.ticked ? 'Untick' : 'Tick'}
        </button>
      </td>
    </tr>
  );
})}

        </tbody>
      </table>
    </div>
  );
};

export default DoWorkout;
