import './App.css';
import { useState, useEffect } from "react"; 
import Axios from 'axios';
import {BrowserRouter as Router, Routes, Route, BrowserRouter} from 'react-router-dom'
import AddWorkout from './pages/AddWorkout';


function App() {
  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState(0);
  const [reps, setReps] = useState("");
  const [weight, setWeight] = useState("");
  const [exerciseList, setExerciseList] = useState([]);
  const [showExercises, setShowExercises] = useState(false);
  const [tableName, setTableName] = useState(""); 

  const addFullExercise = () => {
    Axios.post('http://localhost:3001/addExercise', {
      tableName: tableName, // Send the table name along with exercise data
      exercise: exercise,
      sets: sets,
      reps: reps,
      weight: weight
    }).then(() => {
      setExerciseList([...exerciseList, { // Update exercise list after adding exercise
        exercise: exercise,
        sets: sets,
        reps: reps,
        weight: weight
      }]);
      // Clear input fields after adding exercise
      setExercise("");
      setSets(0);
      setReps("");
      setWeight("");
    }).catch(error => {
      console.error('Error adding exercise:', error);
    });
  };

  return (
    <div className="App">
      <div className="information">
        <label>Add Workout</label>
        <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)}/>
        <button onClick={addFullExercise}>Add Exercise</button>
        {/* Other input fields */}
      </div>
      {/* Exercise display */}
    </div>
  );
}

export default App;



