import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checklist, setChecklist] = useState([]);

  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data);
        console.log(response);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Set Checklist values to false based on the length of tableData
  useEffect(() => {
    if (tableData.length > 0) {
      setChecklist(Array(tableData.length).fill(false));
    }
  }, [tableData]);

  const handleTick = (id) => {
    const index = tableData.findIndex(exercise => exercise.id === id);
    if (index !== -1) {
      const newChecklist = [...checklist];
      newChecklist[index] = !newChecklist[index];
      setChecklist(newChecklist);
    }
  };

  if (tableData.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
            <th>Checklist</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((exercise) => (
            <tr key={exercise.id}>
              <td>{exercise.Exercise}</td>
              <td>{exercise.Sets}</td>
              <td>{exercise.Reps}</td>
              <td>{exercise.Weight}</td>
              <td>
                <button className="tick" onClick={() => handleTick(exercise.id)}>
                  {/*{checklist[tableData.findIndex(ex => ex.id === exercise.id)].toString()}  Display true/false */}
                  {exercise.id}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WorkoutTable;
