import React, { useState } from 'react';
import Axios from 'axios';
import './AddWorkoutStyle.css';

const AddWorkout = () => {
    const [tableName, setTableName] = useState("");
    const [showMessage, setShowMessage] = useState(false); // State to control the visibility of the message
    const [showInput, setShowInput] = useState(true); // State to control the visibility of the input and button

    const newTable = () => {
        Axios.post('http://localhost:3001/createTable', {
            tableName: tableName,
        }).then(() => {
            setTableName('');
            // Show the message after creating the table
            setShowMessage(true);
            // Hide the input and button
            setShowInput(false);
        }).catch(error => {
            console.error('Error adding exercise:', error);
        });
    };

    const toggleMessage = () => {
        // Toggle the showMessage state
        setShowMessage(!showMessage);
        // If showMessage is false, show the input and button again
        if (!showMessage) {
            setShowInput(true);
        }
    };

    return (
        <div>
            {showInput && (
                <div>
                    <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)} placeholder="Enter Workout Name" />
                    <button onClick={() => newTable()}>
                        Create New Workout
                    </button>
                </div>
            )}
            <button onClick={toggleMessage}>
                {showMessage ? 'Close' : 'Show Message'}
            </button>
            {showMessage && (
                <div>hello</div>
            )}
        </div>
    );
};

export default AddWorkout;
