import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import EditWorkout from '../../EditWorkout/EditWorkout';

const NewTable = () => {
    const [tableName, setTableName] = useState("");
    const [tableCreated, setTableCreated] = useState(false);

    const addTable = () => {
        return new Promise((resolve, reject) => {
            Axios.post(`http://localhost:3001/newWorkout/${tableName}`, {
                tableName: tableName
            })
            .then(response => {
                console.log('Table created successfully');
                setTableCreated(true);
                resolve(); // Resolve the promise when table creation is successful
            })
            .catch(error => {
                console.error('Error creating table:', error);
                reject(error); // Reject the promise if there's an error
            });
        });
    };

    const handleCreateWorkout = () => {
        addTable()
            .then(() => {
                // Redirect to the specified link after table creation
                window.location.href = `/table/editWorkout/${tableName}`;
            })
            .catch(error => {
                console.error('Failed to create table:', error);
            });
    };

    return (
        <div className="bg-black text-gray-100 h-screen">
            {!tableCreated && (
                <div className='text-black'>
                    <label>Add Workout</label>
                    <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)}/>
                    <button className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600" onClick={handleCreateWorkout}>Create Workout</button>
                </div>
            )}
        </div>
    );
};

export default NewTable;
