import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import './workoutTableStyles.css'

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checkedRows, setCheckedRows] = useState({});

  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        const data = response.data;
        setTableData(data);
        // Initialize checkedRows with exercise IDs as keys and false as initial values
        const initialCheckedRows = data.reduce((acc, exercise) => {
          acc[exercise.id] = false;
          return acc;

        }, {});
        setCheckedRows(initialCheckedRows);
        console.log(response);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Toggle the checked state for the given row ID
  const toggleRowChecked = (id) => {
    setCheckedRows(prevState => ({
      ...prevState,
      [id]: !prevState[id] // Toggle the checked state
    }));
  };

  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
            <th>Completed</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((exercise) => (
            <tr key={exercise.id} className={checkedRows[exercise.id] ? 'checked' : ''}>
              <td>{exercise.Exercise}</td>
              <td>{exercise.Sets}</td>
              <td>{exercise.Reps}</td>
              <td>{exercise.Weight}</td>
              <td>
                <button className="tick" onClick={() => toggleRowChecked(exercise.id)}>
                  âœ”
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WorkoutTable;
