      <input type="text" value={exerciseToUpdate} onChange={(event) => setExerciseToUpdate(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
      <input type="number" value={setsToUpdate} onChange={(event) => setSetsToUpdate(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
      <input type="text" value={repsToUpdate} onChange={(event) => setRepsToUpdate(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
      <input type="text" value={weightToUpdate} onChange={(event) => setWeightToUpdate(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
