import './App.css';
import {useState} from "react"; 
import Axios from 'axios'


function App() {

  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState(0);
  const [reps, setReps] = useState("")
  const [weight, setWeight] = useState("")

  const [exerciseList, setExerciseList] = useState([])

  const getExercises = () => {
    Axios.get('http://localhost:3001/exercises'
    ).then((response)=>setExerciseList(response.data))
  };

  const deleteExercise = (id) => {
    Axios.delete(`http://localhost:3001/exercise/${id}`)
      .then(() => {
        // Remove the deleted exercise from the exerciseList state
        setExerciseList(exerciseList.filter(exercise => exercise.id !== id));
      })
      .catch(error => console.error('Error deleting exercise:', error));
  };

  const addFullExercise = () => {
    Axios.post('http://localhost:3001/create', {
      exercise: exercise,
      sets: sets,
      reps: reps,
      weight: weight
    }).then(() => {
      // Update the exerciseList state with the new exercise object
      setExerciseList([...exerciseList, {
        exercise: exercise,
        sets: sets,
        reps: reps,
        weight: weight
      }]);
    }).catch(error => {
      console.error('Error adding exercise:', error);
    });
  };



  return (
    <div className="App">
      <div className="information">
        <label>Exercise:</label>
        <input type="text" onChange={(event) => (setExercise(event.target.value))}/>
        <label>No. of Sets:</label>
        <input type="number" onChange={(event) => (setSets(event.target.value))}/>
        <label>Rep Break Down:</label>
        <input type ="text" onChange={(event) => (setReps(event.target.value))}/>
        <label>Weight Breakdown</label>
        <input type = "text" onChange={(event) => (setWeight(event.target.value))}/>
        <button onClick={addFullExercise}>Add Exercise</button>
      </div>

      <div className="exercises">
         <button  onClick={getExercises}>Show Exercises</button>

         {exerciseList.map((val, key) => (
            <div className="exercise" key={key}>
              <h3>{val.exercise}</h3>
              <h3>{val.sets}</h3>
              <h3>{val.reps}</h3>
              <h3>{val.weights}</h3>
            </div>
        ))}
         <div>


         </div>
      </div>

    </div>
  );
}

export default App;
