import './App.css';
import {useState} from "react"; 
import Axios from 'axios'


function App() {

  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState(0);
  const [reps, setReps] = useState("")
  const [weight, setWeight] = useState("")

  const displayInfo = () => {
    console.log(exercise + sets + reps + weight)
  };

  const addFullExercise = () => {
    Axios.post('http://localhost:3001/create', {
      exercisee: exercise, sets: sets, reps:reps, weight: weight
    }).then(() => {
      console.log("SUCCESS")
    });
  };


  return (
    <div className="App">
      <div className="information">
        <label>Exercise:</label>
        <input type="text" onChange={(event) => (setExercise(event.target.value))}/>
        <label>No. of Sets:</label>
        <input type="number" onChange={(event) => (setSets(event.target.value))}/>
        <label>Rep Break Down:</label>
        <input type ="text" onChange={(event) => (setReps(event.target.value))}/>
        <label>Weight Breakdown</label>
        <input type = "text" onChange={(event) => (setWeight(event.target.value))}/>
        <button onClick={addFullExercise}>Add Exercise</button>
      </div>

    </div>
  );
}

export default App;
