import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import Axios from 'axios';
import './workoutTableStyles.css';

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checkedRows, setCheckedRows] = useState({});

  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        const data = response.data;
        setTableData(data);

        // Initialize checkedRows with exercise IDs as keys and false as initial values
        
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName]);

  // Toggle the checked state for the given row ID


  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((exercise) => (
            <tr
              key={exercise.id}
            >
              <td>{exercise.Exercise}</td>
              <td>{exercise.Sets}</td>
              <td>{exercise.Reps}</td>
              <td>{exercise.Weight}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <ul>
        <li><Link to="/table/doWorkout/:tableName"> Begin Workout</Link></li>
        <li><Link to="/table/editWorkout/:tableName"> Edit Workout</Link></li>
      </ul>
    </div>
  );
};

export default WorkoutTable;
