import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link
import Axios from 'axios';

const AllWorkouts = () => {
  const [tableNames, setTableNames] = useState([]);

  useEffect(() => {
    getTableNames();
  }, []);

  const getTableNames = () => {
    Axios.get('http://localhost:3001/tables')
      .then((response) => {
        setTableNames(response.data);
      })
      .catch((error) => {
        console.error('Error fetching table names:', error);
      });
  };

  return (
    <div className="container mx-auto py-8 h-80vh">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">My Workouts</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tableNames.map((table, index) => (
          <div key={index} className="bg-white p-4 shadow-md rounded-md">
            {/* Use Link component to navigate to the table page */}
            <Link to={`/table/${table}`} key={index} className="block text-xl text-gray-800 hover:text-blue-500">
              {table}
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllWorkouts;
