import React, { useState } from 'react';
import Axios from 'axios';
import './AddWorkoutStyle.css';

const AddWorkout = () => {
    const [tableName, setTableName] = useState("");
    const [createWorkout, setCreateWorkout] = useState(false);
    const [showMessage, setShowMessage] = useState(false); // State to control the visibility of the message

    const newTable = () => {
        Axios.post('http://localhost:3001/createTable', {
            tableName: tableName,
        }).then(() => {
            setTableName('');
            // Show the message after creating the table
            setShowMessage(true);
        }).catch(error => {
            console.error('Error adding exercise:', error);
        });
    };

    const handleClose = () => {
        // Close the workout creation section and hide the message
        setCreateWorkout(false);
        setShowMessage(false);
    };

    return (
        <div>
            <button onClick={() => { setCreateWorkout(!createWorkout) }}>
                {createWorkout ? 'Close' : 'Create Workout'}
            </button>
            {createWorkout && (
                <div>
                    <input type="text" value={tableName} onChange={(event) => setTableName(event.target.value)} />
                    <button onClick={() => {
                        newTable();
                    }}>
                        Start Adding exercises
                    </button>
                </div>
            )}
            {showMessage && (
                <div>hello</div>
            )}
        </div>
    );
};

export default AddWorkout;
