// WorkoutTable.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checklist, setChecklist] = useStateArray()

  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data);
        console.log(response);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  //Settting Checklist values to false
  useEffect(()=> {
    setChecklist(tableData.length.fill(false))
  }, [])

  const handleTick = (index) => {

    const newChecklist = [...checklist];
    // Modify the desired element (for example, toggle its value)
    newChecklist[index] = !newChecklist[index];
    // Set the state with the modified array
    setChecklist(newChecklist);
  }



  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
        <tr>
                <th>Name</th>
                <th>Sets</th>
                <th>Reps</th>
                <th>Weight</th>
              </tr>
        </thead>
        <tbody>
              {tableData.map(exercise => (
                <tr key={exercise.id}>
                  <td>{exercise.Exercisel}</td>
                  <td>{exercise.Sets}</td>
                  <td>{exercise.Reps}</td>
                  <td>{exercise.Weight}</td>
                  <td><button className = "tick" onClick = {handleTick(exercise.id)}>{checklist[exercise.id]}</button></td>
                </tr>
              ))}
            </tbody>
      </table>
      <div>
        
      </div>
      {/*}
      <div>
        <Link to={``} >
              <p>Start Workout</p>
        </Link>
        </div>
    <div>
        <Link to={``} >
              <p>Edit Workout</p>
        </Link>
            </div> */}
      


    </div>
  );
};

export default WorkoutTable;
