// WorkoutTable.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [checklist, setChecklist] = useState([]);

  useEffect(() => {
    // Fetch data based on the table name
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data);
        console.log(response);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Set Checklist values to false based on the length of tableData
  useEffect(() => {
    setChecklist(Array(tableData.length).fill(false));
  }, [tableData]);

  const handleTick = (index) => {
    const newChecklist = [...checklist];
    // Modify the desired element (for example, toggle its value)
    newChecklist[index] = !newChecklist[index];
    // Set the state with the modified array
    setChecklist(newChecklist);
  };

  return (
    <div>
      <h2>{tableName}</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Sets</th>
            <th>Reps</th>
            <th>Weight</th>
            <th>Checklist</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((exercise, index) => (
            <tr key={exercise.id}>
              <td>{exercise.Exercise}</td>
              <td>{exercise.Sets}</td>
              <td>{exercise.Reps}</td>
              <td>{exercise.Weight}</td>
              <td>
                <button className="tick" onClick={() => handleTick(exercise.id)}>
                  {checklist[exercise.id].toString()} {/* Display true/false */}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WorkoutTable;
