import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import Axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle, faO } from '@fortawesome/free-solid-svg-icons';

const DoWorkout = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data.map(exercise => ({ ...exercise, ticked: false })));
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName]);

  const handleTick = (id) => {
    setTableData(prevTableData => {
      return prevTableData.map((exercise) => {
        if (exercise.id === id) {
          return { ...exercise, ticked: !exercise.ticked };
        }
        return exercise;
      });
    });
  };

  return (
    <div className="bg-black py-8 w-full h-screen">
      <div className="container mx-auto px-4">
        <h1 className="text-2xl font-bold mb-4 text-green-500 text-center">{tableName}</h1>
        <table className="w-full">
          <thead className="text-green-500">
            <tr>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Sets</th>
              <th className="px-4 py-2">Reps</th>
              <th className="px-4 py-2">Weight</th>
              <th className="px-4 py-2">Completed</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((exercise) => (
              <tr key={exercise.id} className={exercise.ticked ? 'bg-Green text-lightGray' : 'bg-darkGray text-lightGray'}>
                <td className="px-4 py-2">{exercise.Exercise}</td>
                <td className="px-4 py-2">{exercise.Sets}</td>
                <td className="px-4 py-2">{exercise.Reps}</td>
                <td className="px-4 py-2">{exercise.Weight}</td>
                <td className="px-4 py-2">
                  <button onClick={() => handleTick(exercise.id)}>
                    {exercise.ticked ? <FontAwesomeIcon icon={faCircle} /> : <FontAwesomeIcon icon={faO} />} 
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="mt-4 flex justify-center">
          <Link to="/" className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">Finish</Link>
        </div>
      </div>
    </div>
  );
};

export default DoWorkout;
