import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { Link } from 'react-router-dom';

const EditWorkout = () => {
  const [exercise, setExercise] = useState("");
  const [sets, setSets] = useState(0);
  const [reps, setReps] = useState("");
  const [weight, setWeight] = useState("");
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);
  const [updateExercise, setUpdateExercise] = useState (false)
  const[ exerciseUpdateId, setExerciseUpdateId] = useState('')

  useEffect(() => {
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        setTableData(response.data.map(exercise => ({ ...exercise, ticked: false })));
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableData, tableName]);

  const deleteExercise = (id) => {
    Axios.delete(`http://localhost:3001/tables/${tableName}/delete/${id}`)
      .then(() => {
        setTableData(tableData.filter(exercise => exercise.id !== id));
      })
      .catch(error => console.error('Error deleting exercise:', error));
  };

  const addFullExercise = () => {
    Axios.post(`http://localhost:3001/create/${tableName}`, {
      exercise: exercise,
      sets: sets,
      reps: reps,
      weight: weight
    }).then(() => {
      setTableData([...tableData, {
        exercise: exercise,
        sets: sets,
        reps: reps,
        weight: weight
      }]);
      setExercise("");
      setSets(0);
      setReps("");
      setWeight("");
    }).catch(error => {
      console.error('Error adding exercise:', error);
    });
  };


  const triggerEdit = (id) =>{
    setUpdateExercise(true)
    setExerciseUpdateId(id)
  }

  const editExercise =(id)=> {
    Axios.put(`http://localhost:3001/tables/${tableName}/edit`, {
      exercise: exercise,
      sets: sets,
      reps: reps,
      weight: weight
    }).then(() => {
      setTableData(tableData.filter(exercise => exercise.id !== exerciseUpdateId));
      setTableData([...tableData, {
        exercise: exercise,
        sets: sets,
        reps: reps,
        weight: weight
      }]);
      setExercise("");
      setSets(0);
      setReps("");
      setWeight("");
    }).catch(error => {
      console.error('Error updating exercise:', error);
    });

  }

  return (
    <div className="bg-black py-8 h-screen">
      <div className="container mx-auto px-4">
        <h2 className="text-Green font-bold text-center text-2xl mb-4">{tableName}</h2>
        <div className="overflow-x-auto sm:overflow-x-visible">
          <div className="flex flex-col">
            <div className="flex bg-Green text-darkGray font-bold text-center py-2 px-4 rounded-md  mb-4">
              <div className="w-1/4 sm:w-1/5">Name</div>
              <div className="w-1/4 sm:w-1/5">Sets</div>
              <div className="w-1/4 sm:w-1/5">Reps</div>
              <div className="w-1/4 sm:w-1/5">Weight</div>
              <div className="w-1/4 sm:w-1/5">Action</div>
            </div>

            {/*Conditional rendering based on view or edit mode*/}
            {tableData.map((exercise) => (
              updateExercise && exercise.id === exerciseUpdateId ? (
                
                <div className="flex bg-darkGray text-white py-2 px-4 rounded-md  mb-2">
                  <input type="text" value={exercise} onChange={(event) => setExercise(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
                  <input type="number" value={sets} onChange={(event) => setSets(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
                  <input type="text" value={reps} onChange={(event) => setReps(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
                  <input type="text" value={weight} onChange={(event) => setWeight(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md"/>
                  <div className="w-1/4 sm:w-1/5 flex justify-center">
                    <button onClick={() => editExercise(exercise.id)} className="bg-Green text-white px-2 py-1 rounded-md hover:bg-green-600">Update</button>
                  </div>
              </div>
              ) : (
                //view mode
                <div>
                  <div className="flex bg-darkGray text-white py-2 px-4 rounded-md  mb-2">
                  <div key={exercise.id} className="flex bg-darkGray text-lightGray text-center text-bold py-2 px-4n rounded-md  mb-1">
                    <div className="w-1/4 sm:w-1/5">{exercise.exercise}</div>
                    <div className="w-1/4 sm:w-1/5">{exercise.sets}</div>
                    <div className="w-1/4 sm:w-1/5">{exercise.reps}</div>
                    <div className="w-1/4 sm:w-1/5">{exercise.weight}</div>
                    <div className="w-1/4 sm:w-1/5 flex justify-center">
                      <button className="bg-red-600 text-lightGray mx-auto px-2 py-1 rounded-md hover:bg-red-800" onClick={() => deleteExercise(exercise.id)}>Delete</button>
                      <button className="bg-yellow-600 text-lightGray mx-auto px-2 py-1 rounded-md hover:bg-yellow-800" onClick={() => triggerEdit(exercise.id)}>Edit</button>
                    </div>
                  </div>
    
                  </div>
                </div>
                
              )
              
            ))}
            <div className="flex bg-darkGray text-white py-2 px-4 rounded-md  mb-2">
              <input type="text" value={exercise} onChange={(event) => setExercise(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
              <input type="number" value={sets} onChange={(event) => setSets(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
              <input type="text" value={reps} onChange={(event) => setReps(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md" />
              <input type="text" value={weight} onChange={(event) => setWeight(event.target.value)} className="bg-lightGray w-1/4 text-darkGray mx-2 sm:w-1/5 px-2 py-1 rounded-md"/>
              <div className="w-1/4 sm:w-1/5 flex justify-center">
                <button onClick={addFullExercise} className="bg-Green text-white px-2 py-1 rounded-md hover:bg-green-600">Add Exercise</button>
              </div>
            </div>
          </div>
        </div>
      </div >
      <div className="mt-4 flex justify-center">
          <Link to="/" className="bg-Green text-white px-4 py-2 rounded-md hover:bg-green-600">Finish</Link>
        </div>
    </div>
  );
};

export default EditWorkout;
