import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import Axios from 'axios';

const WorkoutTable = () => {
  const { tableName } = useParams();
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    Axios.get(`http://localhost:3001/tables/${tableName}`)
      .then((response) => {
        const data = response.data;
        setTableData(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [tableName]);

  return (
    <div className="bg-black py-8 w-full h-screen">
      <div className="container mx-auto px-4">
        <h1 className="text-2xl font-bold mb-4 text-green-500 text-center">{tableName}</h1>
        <table className="w-full border border-lightGray rounded-lg shadow-lg">
          <thead className="bg-darkGray text-white">
            <tr>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Sets</th>
              <th className="px-4 py-2">Reps</th>
              <th className="px-4 py-2">Weight</th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((exercise, index) => (
              <tr key={index} className="bg-darkGray text-white">
                <td className="px-4 py-2">{exercise.Exercise}</td>
                <td className="px-4 py-2">{exercise.Sets}</td>
                <td className="px-4 py-2">{exercise.Reps}</td>
                <td className="px-4 py-2">{exercise.Weight}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="mt-4 flex justify-center">
          <Link to={`/table/doWorkout/${tableName}`} className="mr-4 bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">Begin Workout</Link>
          <Link to={`/table/editWorkout/${tableName}`} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">Edit Workout</Link>
        </div>
      </div>
    </div>
  );
};

export default WorkoutTable;
